member.example:
[EnumDescription("中文数字")]
enum MyEnum
{
	[EnumDescription("数字一")]
	One = 1, 

	[EnumDescription("数字二")]
	Two, 

	[EnumDescription("数字三")]
	Three
}
EnumDescription.GetEnumText(typeof(MyEnum));
EnumDescription.GetFieldText(MyEnum.Two);
EnumDescription.GetFieldTexts(typeof(MyEnum));

